const agora = new Date();
const hora = agora.getHours();

let min, max;

if ((hora >= 6 && hora < 10) || (hora >= 13 && hora < 16) || (hora >= 20 && hora <= 23)) {
    min = 0.3;
    max = 0.5;
} else if (hora >= 10 && hora < 13) {
    min = 0.6;
    max = 0.9;
} else if (hora >= 17 && hora < 20) {
    min = 0.8;
    max = 1;
} else {
    min = 0;
    max = 0;
}

const porcentagemUtilizacao = Math.floor(Math.random() * (max - min + 1)) + min;
msg.payload = 0.3 * porcentagemUtilizacao * msg.payload;
msg.topic = "Equipamentos Ergométricos"
return msg;
